liAgencia` on((`cliente`.`branch` = `cliAgencia`.`c_bpartner_location_id`))) left join `c_bpartner_location` `prodAgencia` on((`deposito`.`agencia_id` = `prodAgencia`.`c_bpartner_location_id`))) left join `ctbconf_comprobante` `comprobante` on((`dep_movimiento`.`ctbconf_comprobante_id` = `comprobante`.`ctbconf_comprobante_id`))) left join `ctbconf_tipo_doc` `tipo_doc` on((`dep_movimiento`.`ctbconf_tipo_doc_id` = `tipo_doc`.`ctbconf_tipo_doc_id`))) WHERE ((`dep_movimiento`.`fecha_movimiento` BETWEEN '2020-01-31' AND '2021-02-01') AND (`canalref`.`name` = 'Caja') OR (`canalref`.`name` = 'Debito Automatico') OR (`canalref`.`name` = 'Distribución de Fondos') OR (`canalref`.`name` = 'Distribuciones') OR (`canalref`.`name` = 'Internet Banking') OR (`canalref`.`name` = 'PDA') OR (`canalref`.`name` = 'Red') OR (`canalref`.`name` = 'REEXPEDICIÓN RED')  OR (`canalref`.`name` = 'Traslados Internos' AND NOT `tipo_doc`.`descripcion` = 'Cobro Cuota de Manejo TD' AND NOT `tipo_doc`.`descripcion` = 'Liquidacion Intereses Depositos' AND NOT `tipo_doc`.`descripcion` = 'Retiro de Clientes' AND NOT `tipo_doc`.`descripcion` = 'Reversos Depositos' AND NOT `tipo_doc`.`descripcion` = 'Reversos Taquilla'))  