select `dep`.`cu_customermaster_id` AS `cliente_id`,sum(`dep`.`saldo_actual`) AS `saldo`, `cliente`.`identification` AS `cedula` from ((`dep_deposito` `dep` left join `prd_producto` `pro` on((`dep`.`prd_producto_id` = `pro`.`prd_producto_id`))) left join `prd_tipo_producto` `tip` on((`pro`.`prd_tipo_producto_id` = `tip`.`prd_tipo_producto_id`)))join `cu_customermaster` `cliente` on `cliente`.`cu_customermaster_id`=`dep`.`cu_customermaster_id` where (`tip`.`clasificacion` = 'AT') group by `dep`.`cu_customermaster_id`